Hvad taler vi egentlig om?
{https://x.com/marcobravoram/status/1241781966497710080}

Maskinlæring er værktøjet vi bruger til at opnå kunstig intelligens. Grænsen er flydende. Vi taler ikke her om kunstig intelligens i almindelighed. Vi taler om de store tekstmodeller. De kan så interface med maskinlæring, og trække på metoder til billedgenerering, billedanalyse etc.

Bare fordi. ggbiplot er en lækker pakke. Det kan bare være en lidt frustrerende oplevelse at få den installeret. Der er bøvl med forskellige versioner af R, og når 60 studerende på et kursus alle prøver at installere fra github samtidig, støder man næsen mod begrænsninger i githubs api. Så. Kopier nedenstående, kør det i R, og funktionen ggbiplot er tilgængelig.

ggbiplot &lt;- function (pcobj, choices = 1:2, scale = 1, pc.biplot = TRUE, 
    obs.scale = 1 - scale, var.scale = scale, groups = NULL, 
    ellipse = FALSE, ellipse.prob = 0.68, labels = NULL, labels.size = 3, 
    alpha = 1, var.axes = TRUE, circle = FALSE, circle.prob = 0.69, 
    varname.size = 3, varname.adjust = 1.5, varname.abbrev = FALSE, 
    ...) 
{
    library(ggplot2)
    library(plyr)
    library(scales)
    library(grid)
    stopifnot(length(choices) == 2)
    if (inherits(pcobj, "prcomp")) {
        nobs.factor &lt;- sqrt(nrow(pcobj$x) - 1)
        d &lt;- pcobj$sdev
        u &lt;- sweep(pcobj$x, 2, 1/(d * nobs.factor), FUN = "*")
        v &lt;- pcobj$rotation
    }
    else if (inherits(pcobj, "princomp")) {
        nobs.factor &lt;- sqrt(pcobj$n.obs)
        d &lt;- pcobj$sdev
        u &lt;- sweep(pcobj$scores, 2, 1/(d * nobs.factor), FUN = "*")
        v &lt;- pcobj$loadings
    }
    else if (inherits(pcobj, "PCA")) {
        nobs.factor &lt;- sqrt(nrow(pcobj$call$X))
        d &lt;- unlist(sqrt(pcobj$eig)[1])
        u &lt;- sweep(pcobj$ind$coord, 2, 1/(d * nobs.factor), FUN = "*")
        v &lt;- sweep(pcobj$var$coord, 2, sqrt(pcobj$eig[1:ncol(pcobj$var$coord), 
            1]), FUN = "/")
    }
    else if (inherits(pcobj, "lda")) {
        nobs.factor &lt;- sqrt(pcobj$N)
        d &lt;- pcobj$svd
        u &lt;- predict(pcobj)$x/nobs.factor
        v &lt;- pcobj$scaling
        d.total &lt;- sum(d^2)
    }
    else {
        stop("Expected a object of class prcomp, princomp, PCA, or lda")
    }
    choices &lt;- pmin(choices, ncol(u))
    df.u &lt;- as.data.frame(sweep(u[, choices], 2, d[choices]^obs.scale, 
        FUN = "*"))
    v &lt;- sweep(v, 2, d^var.scale, FUN = "*")
    df.v &lt;- as.data.frame(v[, choices])
    names(df.u) &lt;- c("xvar", "yvar")
    names(df.v) &lt;- names(df.u)
    if (pc.biplot) {
        df.u &lt;- df.u * nobs.factor
    }
    r &lt;- sqrt(qchisq(circle.prob, df = 2)) * prod(colMeans(df.u^2))^(1/4)
    v.scale &lt;- rowSums(v^2)
    df.v &lt;- r * df.v/sqrt(max(v.scale))
    if (obs.scale == 0) {
        u.axis.labs &lt;- paste("standardized PC", choices, sep = "")
    }
    else {
        u.axis.labs &lt;- paste("PC", choices, sep = "")
    }
    u.axis.labs &lt;- paste(u.axis.labs, sprintf("(%0.1f%% explained var.)", 
        100 * pcobj$sdev[choices]^2/sum(pcobj$sdev^2)))
    if (!is.null(labels)) {
        df.u$labels &lt;- labels
    }
    if (!is.null(groups)) {
        df.u$groups &lt;- groups
    }
    if (varname.abbrev) {
        df.v$varname &lt;- abbreviate(rownames(v))
    }
    else {
        df.v$varname &lt;- rownames(v)
    }
    df.v$angle &lt;- with(df.v, (180/pi) * atan(yvar/xvar))
    df.v$hjust = with(df.v, (1 - varname.adjust * sign(xvar))/2)
    g &lt;- ggplot(data = df.u, aes(x = xvar, y = yvar)) + xlab(u.axis.labs[1]) + 
        ylab(u.axis.labs[2]) + coord_equal()
    if (var.axes) {
        if (circle) {
            theta &lt;- c(seq(-pi, pi, length = 50), seq(pi, -pi, 
                length = 50))
            circle &lt;- data.frame(xvar = r * cos(theta), yvar = r * 
                sin(theta))
            g &lt;- g + geom_path(data = circle, color = muted("white"), 
                size = 1/2, alpha = 1/3)
        }
        g &lt;- g + geom_segment(data = df.v, aes(x = 0, y = 0, 
            xend = xvar, yend = yvar), arrow = arrow(length = unit(1/2, 
            "picas")), color = muted("red"))
    }
    if (!is.null(df.u$labels)) {
        if (!is.null(df.u$groups)) {
            g &lt;- g + geom_text(aes(label = labels, color = groups), 
                size = labels.size)
        }
        else {
            g &lt;- g + geom_text(aes(label = labels), size = labels.size)
        }
    }
    else {
        if (!is.null(df.u$groups)) {
            g &lt;- g + geom_point(aes(color = groups), alpha = alpha)
        }
        else {
            g &lt;- g + geom_point(alpha = alpha)
        }
    }
    if (!is.null(df.u$groups) && ellipse) {
        theta &lt;- c(seq(-pi, pi, length = 50), seq(pi, -pi, length = 50))
        circle &lt;- cbind(cos(theta), sin(theta))
        ell &lt;- ddply(df.u, "groups", function(x) {
            if (nrow(x) &lt;= 2) {
                return(NULL)
            }
            sigma &lt;- var(cbind(x$xvar, x$yvar))
            mu &lt;- c(mean(x$xvar), mean(x$yvar))
            ed &lt;- sqrt(qchisq(ellipse.prob, df = 2))
            data.frame(sweep(circle %*% chol(sigma) * ed, 2, 
                mu, FUN = "+"), groups = x$groups[1])
        })
        names(ell)[1:2] &lt;- c("xvar", "yvar")
        g &lt;- g + geom_path(data = ell, aes(color = groups, group = groups))
    }
    if (var.axes) {
        g &lt;- g + geom_text(data = df.v, aes(label = varname, 
            x = xvar, y = yvar, angle = angle, hjust = hjust), 
            color = "darkred", size = varname.size)
    }
    return(g)
}

